import React from "react";
import ReactDOM from "react-dom";

import ReactDataGrid from "react-data-grid";

const groupings = [
  {
    title: "Assets",
    descriptions: [
      "$ Allowance for Loan Losses",
      "$ Restricted Cash",
      "$ Current Assets",
      "$ Total Assets",
      "$ Unrestricted Cash & Cash Equivalents",
      "$ Current Portion of Notes Receiveable"
    ]
  },
  {
    title: "Assets2",
    descriptions: [
      "$ Allowance for Loan Losses",
      "$ Restricted Cash",
      "$ Current Assets",
      "$ Total Assets",
      "$ Unrestricted Cash & Cash Equivalents",
      "$ Current Portion of Notes Receiveable"
    ]
  }
];

const columns = [
  { key: "category", name: "Category" },
  { key: "description", name: "Description" },
  { key: "2016", name: "2016", editable: true },
  { key: "2017", name: "2017", editable: true },
  { key: "2018", name: "2018", editable: true },
  { key: "2019", name: "2019", editable: true },
  { key: "projected", name: "Projected", editable: true }
];

const rows = groupings.reduce((rows, t) => {
  const descripts = t.descriptions.map((d, idx) => {
    return {
      category: idx === 0 ? t.title : "",
      description: d,
      "2016": "$0.00",
      "2017": "$0.00",
      "2018": "$0.00",
      "2019": "$0.00",
      projected: "$0.00"
    };
  });

  return rows.concat(descripts);
}, []);

const defaultParsePaste = str =>
  str.split(/\r\n|\n|\r/).map(row => row.split("\t"));

class GridOne extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      rows: rows,
      topLeft: {},
      botRight: {}
    };

    // Copy paste event handler
    document.addEventListener("copy", this.handleCopy);
    document.addEventListener("paste", this.handlePaste);
  }

  componentWillUnmount() {
    this.removeAllListeners();
  }

  removeAllListeners = () => {
    document.removeEventListener("copy", this.handleCopy);
    document.removeEventListener("paste", this.handlePaste);
  };

  handleCopy = e => {
    console.debug("handleCopy Called");
    e.preventDefault();
    const { topLeft, botRight } = this.state;
    function range(size, startAt = 0) {
        return [...Array(size).keys()].map(i => i + startAt);
    }
    // Loop through each row
    const text = range(topLeft.rowIdx, botRight.rowIdx + 1)
      .map(
        // Loop through each column
        rowIdx =>
          columns
            .slice(topLeft.colIdx, botRight.colIdx + 1)
            .map(
              // Grab the row values and make a text string
              col => this.rowGetter(rowIdx)[col.key]
            )
            .join("\t")
      )
      .join("\n");
    console.debug("text", text);
    e.clipboardData.setData("text/plain", text);
  };

  handlePaste = e => {
    console.debug("handlePaste Called");
    e.preventDefault();
    const { topLeft } = this.state;

    const newRows = [];
    const pasteData = defaultParsePaste(e.clipboardData.getData("text/plain"));

    console.debug("pasteData", pasteData);

    pasteData.forEach(row => {
      const rowData = {};
      // Merge the values from pasting and the keys from the columns
      columns
        .slice(topLeft.colIdx, topLeft.colIdx + row.length)
        .forEach((col, j) => {
          // Create the key-value pair for the row
          rowData[col.key] = row[j];
        });
      // Push the new row to the changes
      newRows.push(rowData);
    });

    console.debug("newRows", newRows);

    this.updateRows(topLeft.rowIdx, newRows);
  };

  onGridRowsUpdated = ({ fromRow, toRow, updated, action }) => {
    console.debug("onGridRowsUpdated!", action);
    console.debug("updated", updated);
    if (action !== "COPY_PASTE") {
      this.setState(state => {
        const rows = state.rows.slice();
        for (let i = fromRow; i <= toRow; i++) {
          rows[i] = { ...rows[i], ...updated };
        }
        return { rows };
      });
    }
  };

  setSelection = args => {
    this.setState({
      topLeft: {
        rowIdx: args.topLeft.rowIdx,
        colIdx: args.topLeft.idx
      },
      botRight: {
        rowIdx: args.bottomRight.rowIdx,
        colIdx: args.bottomRight.idx
      }
    });
  };

  updateRows = (startIdx, newRows) => {
    this.setState(state => {
      const rows = state.rows.slice();
      for (let i = 0; i < newRows.length; i++) {
        if (startIdx + i < rows.length) {
          rows[startIdx + i] = { ...rows[startIdx + i], ...newRows[i] };
        }
      }
      return { rows };
    });
  };

  render() {
    return (
      <ReactDataGrid
        columns={columns}
        rowGetter={i => this.state.rows[i]}
        rowsCount={rows.length}
        onGridRowsUpdated={this.onGridRowsUpdated}
        enableCellSelect={true}
        cellRangeSelection={{
          onComplete: this.setSelection
        }}
      />
    );
  }
}

export default GridOne;
