import React, { createRef, useState } from "react";
import { createPopper } from "@popperjs/core";
import axios from "axiosConfig";
import config from "config";
import ENUMS from "constants/appEnums";
import { useHistory } from "react-router";
import { useAppContext } from "util/ContextUtil";
import { useDispatch } from "react-redux";
import { useToasts } from "react-toast-notifications";
import allActions from "redux/actions";

const UserDropdown = () => {
    let history = useHistory();
    const { addToast } = useToasts();
    const dispatch = useDispatch();
    const { userHasAuthenticated } = useAppContext();

    // dropdown props
    const btnDropdownRef = createRef();
    const popoverDropdownRef = createRef();
    const [dropdownPopoverShow, setDropdownPopoverShow] = useState(false);

    /**
     * Method to open dropdown
     * @param {*} e 
     */
    const openDropdownPopover = () => {
        createPopper(btnDropdownRef.current, popoverDropdownRef.current, {
            placement: "bottom-start",
        });
        setDropdownPopoverShow(true);
    };

    /**
     * Method to close deopdown
     * @param {*} e 
     */
    const closeDropdownPopover = () => {
        setDropdownPopoverShow(false);
    };

    /**
     * Handle log out
     * @param {*} e 
     */
    const handleLogout = (e) => {
        e.preventDefault();
        // TODO: Remove fake call and data
        logOut({
            data: {
                message: 'Logged out successfully!'
            }
        });
        // TODO ENDS : Remove fake call and data 
        axios.get(config.API_URL + ENUMS.API_ROUTES.AUTH_LOGOUT)
        .then(res => {
            if (res.status === 200) {
                logOut(res.data);
            }
            else {
                console.error("User is not logged out successfully");
            }
        })
        .catch(error => {
            console.error(error);
        })
    };

    const logOut = (data) => {
        userHasAuthenticated(false);
        dispatch(allActions.authActions.userLoggedOut());
        localStorage.removeItem('JWT');
        history.push('/auth/login');
        console.log(data.message)
        addToast(data.message, { 
            appearance: 'success',
            autoDismiss: true
        });
    }

    return (
        <>
        <a
            className="text-blueGray-500 block"
            href="#pablo"
            ref={btnDropdownRef}
            onClick={(e) => {
            e.preventDefault();
            dropdownPopoverShow ? closeDropdownPopover() : openDropdownPopover();
            }}
        >
            <div className="items-center flex">
            <span className="w-12 h-12 text-sm text-white bg-blueGray-200 inline-flex items-center justify-center rounded-full">
                <img
                alt="..."
                className="w-full rounded-full align-middle border-none shadow-lg"
                src={require("assets/img/team-1-800x800.jpg").default}
                />
            </span>
            </div>
        </a>
        <div
            ref={popoverDropdownRef}
            className={
            (dropdownPopoverShow ? "block " : "hidden ") +
            "bg-white text-base z-50 float-left py-2 list-none text-left rounded shadow-lg min-w-48"
            }
        >

            <a
            href="#pablo"
            className={
                "text-sm py-2 px-4 font-normal block w-full whitespace-nowrap bg-transparent text-blueGray-700"
            }
            onClick={(e) => e.preventDefault(e)}
            >
            My Profile
            </a>
            <div className="h-0 my-2 border border-solid border-blueGray-100" />
            <a
            href="#pablo"
            className={
                "text-sm py-2 px-4 font-normal block w-full whitespace-nowrap bg-transparent text-blueGray-700"
            }
            onClick={(e) => handleLogout(e)}
            >
            Log out
            </a>
        </div>
        </>
    );
};

export default UserDropdown;
