import React from "react";
import { Form, Input, Select, Row, Col, Button } from 'antd';
const { Option } = Select;
export default function JobInfo() {
    const [form] = Form.useForm();

    const onFinish = values => {
        console.log('Received values of form: ', values);
    };

    const getNewValue = (values, i, prevValue) => {
        const newIndex = ++i;
        return values[newIndex] ? values[newIndex] : prevValue;
    }

    const handlePaste = (e, form) => {
        var data = e.clipboardData.items[0];

        const values = [];
        data.getAsString(str => {
            str.split(/\s/).forEach((v, ind) => {
              values.push(v);
            })
            const fields = form.getFieldsValue()
            let i = -1;
            form.setFieldsValue({
                ...fields,
                job_name: getNewValue(values, i, fields['job_name']),
                pad_name: getNewValue(values, i, fields['pad_name']),
                job_type: getNewValue(values, i, fields['job_type']),
                pad_id: getNewValue(values, i, fields['pad_id']),
                job_id: getNewValue(values, i, fields['job_id']),
                client_name: getNewValue(values, i, fields['client_name']),
                afe_number: getNewValue(values, i, fields['afe_number']),
                customer_field_rep: getNewValue(values, i, fields['customer_field_rep']),
                country: getNewValue(values, i, fields['country']),
                rep_contact: getNewValue(values, i, fields['rep_contact']),
                basin: getNewValue(values, i, fields['basin']),
                operator: getNewValue(values, i, fields['operator']),
                state: getNewValue(values, i, fields['state']),
                service_company: getNewValue(values, i, fields['service_company']),
                start_date: getNewValue(values, i, fields['start_date']),
                wireline_company: getNewValue(values, i, fields['wireline_company']),
            });
        });

    }
    return (
        <>
            <Form name="complex-form" form={form} onFinish={onFinish} labelCol={{ span: 8 }} wrapperCol={{ span: 16 }}>
                <Row gutter={24}>
                    <Col span={12}>
                        <Form.Item
                            name="job_name"
                            label="Job name"
                            validateTrigger="onBlur"
                            rules={[
                                {
                                    required: true,
                                    message: 'Job name is required!',
                                },
                            ]}
                            onPaste={(e) => handlePaste(e, form)}
                        >
                            <Input placeholder="Job name" />
                        </Form.Item>
                    </Col>
                    <Col span={12}>
                        <Form.Item
                            name="pad_name"
                            label="Pad name"
                            validateTrigger="onBlur"
                            rules={[
                            {
                                required: true,
                                message: 'Pad name is required!',
                            },
                            ]}
                        >
                            <Input placeholder="Pad name" />
                        </Form.Item>
                    </Col>
                </Row>
                <Row gutter={24}>
                    <Col span={12}>
                        <Form.Item
                            name="job_type"
                            label="Job type"
                            validateTrigger="onBlur"
                            rules={[
                            {
                                required: true,
                                message: 'Job type is required!',
                            },
                            ]}
                        >
                            <Input placeholder="Job type" />
                        </Form.Item>
                    </Col>
                    <Col span={12}>
                        <Form.Item
                            name="pad_id"
                            label="Pad Id"
                            validateTrigger="onBlur"
                            rules={[
                            {
                                required: true,
                                message: 'Pad Id is required!',
                            },
                            ]}
                        >
                            <Input placeholder="Pad id" />
                        </Form.Item>
                    </Col>
                </Row>
                <Row gutter={24}>
                    <Col span={12}>
                        <Form.Item
                            name="job_id"
                            label="Job id"
                            validateTrigger="onBlur"
                            rules={[
                            {
                                required: true,
                                message: 'Job id is required!',
                            },
                            ]}
                        >
                            <Input placeholder="Job id" />
                        </Form.Item>
                    </Col>
                    <Col span={12}>
                        <Form.Item
                            name="client_name"
                            label="Client name"
                            validateTrigger="onBlur"
                            rules={[
                            {
                                required: true,
                                message: 'Client name is required!',
                            },
                            ]}
                        >
                            <Input placeholder="Client name" />
                        </Form.Item>
                    </Col>
                </Row>
                <Row gutter={24}>
                    <Col span={12}>
                        <Form.Item
                            name="afe_number"
                            label="AFE #"
                            validateTrigger="onBlur"
                            rules={[
                            {
                                required: true,
                                message: 'AFE # is required!',
                            },
                            ]}
                        >
                            <Input placeholder="AFE #" />
                        </Form.Item>
                    </Col>
                    <Col span={12}>
                        <Form.Item
                            name="customer_field_rep"
                            label="Customer field rep"
                            validateTrigger="onBlur"
                            rules={[
                            {
                                required: true,
                                message: 'Customer field rep is required!',
                            },
                            ]}
                        >
                            <Input placeholder="Customer field rep" />
                        </Form.Item>
                    </Col>
                </Row>
                <Row gutter={24}>
                    <Col span={12}>
                        <Form.Item
                            name="country"
                            label="Country"
                            validateTrigger="onBlur"
                            rules={[
                            {
                                required: true,
                                message: 'Country is required!',
                            },
                            ]}
                        >
                            <Input placeholder="Country" />
                        </Form.Item>
                    </Col>
                    <Col span={12}>
                        <Form.Item
                            name="rep_contact"
                            label="Rep contact #"
                            validateTrigger="onBlur"
                            rules={[
                            {
                                required: true,
                                message: 'Rep contact # is required!',
                            },
                            ]}
                        >
                            <Input placeholder="Rep contact #" />
                        </Form.Item>
                    </Col>
                </Row>
                <Row gutter={24}>
                    <Col span={12}>
                        <Form.Item
                            name="basin"
                            label="Basin"
                            validateTrigger="onBlur"
                            rules={[
                            {
                                required: true,
                                message: 'Basin is required!',
                            },
                            ]}
                        >
                            <Input placeholder="Basin" />
                        </Form.Item>
                    </Col>
                    <Col span={12}>
                        <Form.Item
                            name="operator"
                            label="Operator"
                            validateTrigger="onBlur"
                            rules={[
                            {
                                required: true,
                                message: 'Operator is required!',
                            },
                            ]}
                        >
                            <Input placeholder="Operator" />
                        </Form.Item>
                    </Col>
                </Row>
                <Row gutter={24}>
                    <Col span={12}>
                        <Form.Item
                            name="state"
                            label="State"
                            validateTrigger="onBlur"
                            rules={[
                            {
                                required: true,
                                message: 'State is required!',
                            },
                            ]}
                        >
                            <Input placeholder="State" />
                        </Form.Item>
                    </Col>
                    <Col span={12}>
