import React from "react";
import ReactDOM from "react-dom";

import ReactDataSheet from "react-datasheet";
import "react-datasheet/lib/react-datasheet.css";
import "./Grid.css";
import { width } from "tailwindcss/defaultTheme";

const SheetRenderer = props => {
  console.log(props)
  const { className, columns } = props;
  return (
    <table className={className + " seismos-spreadsheet-grid"}>
      <thead>
        <tr>
          {columns.map((col, index) => (
            <th
              className={`cell ${className}`}
              style={{
                width: col.width,
                background: "#f9f9f9",
                padding: "10px 0px",
                textAlign: "center"
              }}
              colSpan={col.colSpan ? col.colSpan : 1}
            >
              {col.label}
            </th>
          ))}
        </tr>
      </thead>
      <tbody>{props.children}</tbody>
    </table>
  );
};

const CellRenderer = props => {
  const { children } = props;
  return (
    <td className="cell" style={{ textAlign: "center" }}>
      {children}
    </td>
  );
};

class Grid extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      columns: props.columns,
      grid: props.grid,
      className: props.className
    }
    this.renderSheet = this.renderSheet.bind(this);
    this.renderRow = this.renderRow.bind(this);
  }

  renderSheet(props) {
    return <SheetRenderer columns={this.state.columns} {...props} />;
  }

  renderRow(props) {
    const { row, cells, ...rest } = props;
    return <CellRenderer rowIndex={row} {...rest} />;
  }

  render() {
    return (
      <div style={{ width: this.props.width ? this.props.width : "100%" }}>
        <ReactDataSheet
            data={this.state.grid}
            valueRenderer={cell => cell.value}
            sheetRenderer={this.renderSheet}
            className={this.state.className}
            isCellNavigable={(cell, row, col) => {return !cell.disableEvents ? true : false}}
            // cellRenderer={this.renderRow}
            onCellsChanged={changes => {
                const grid = this.state.grid.map(row => [...row]);
                    changes.forEach(({ cell, row, col, value }) => {
                    grid[row][col] = { ...grid[row][col], value };
                });
                this.setState({ grid });
            }}
        />
      </div>
    );
  }
}

export default Grid;
