import config from "config";
import axios from "axiosConfig";
import { Layout, Menu } from 'antd';
import ENUMS from "constants/appEnums";
import allActions from "redux/actions";
import { useHistory } from "react-router";
import { useDispatch } from "react-redux";
import { useAppContext } from "util/ContextUtil";
import React from "react";
import { useToasts } from "react-toast-notifications";

const { Header} = Layout;

export default function Navbar(props) {
    let history = useHistory();
        const dispatch = useDispatch();
        const { addToast } = useToasts();
        const { userHasAuthenticated } = useAppContext();

        const logOutUser = (data) => {
            userHasAuthenticated(false);
            dispatch(allActions.authActions.userLoggedOut());
            localStorage.removeItem('JWT');
            history.push('/auth/login');

            addToast(data.message, { 
                appearance: 'success',
                autoDismiss: true
            });
        }
        const handleLogout = () => {
            logOutUser({
                message: 'Logged out successfully.'
            });
            axios.get(config.API_URL + ENUMS.API_ROUTES.AUTH_LOGOUT)
            .then(res => {
                if (res.status === 200) {
                    logOutUser(res.data);
                }
                else {
                    console.error("User is not logged out successfully");
                }
            })
            .catch(error => {
                console.error(error);
            })
        }

        const handleMenuClick = (e) => {
            if(e.key === "logout") {
                handleLogout();
            }
        }

    return (
        <>
        {/* Navbar */}
        <Header>
            {
                props.withLogo 
                ? <div className="logo-wo-sidebar">
                Seismos
                </div>
                : ''
            }
            <Menu theme="dark" mode="horizontal" className="justify-end" onClick={(e) => handleMenuClick(e)}>
                <Menu.Item key={'account_settings'}>{`Account Settings`}</Menu.Item>
                <Menu.Item key={'logout'}>{`Log out`}</Menu.Item>
            </Menu>
        </Header>
        {/* End Navbar */}
        </>
    );
}
