import React, { useEffect, useState } from "react";
import { Switch, Route, Redirect } from "react-router-dom";
import routes from "routes/routes.js";
// components

import AdminNavbar from "components/Navbars/AdminNavbar.js";
import Sidebar from "components/Sidebar/Sidebar.js";
import ENUMS from "constants/appEnums";
import APP_CONSTANTS from "constants/appConstants";
import { Layout, Menu, Breadcrumb } from 'antd';
import {
    AppstoreOutlined,
    BarChartOutlined,
    CloudOutlined,
    ShopOutlined,
    TeamOutlined,
    UserOutlined,
    UploadOutlined,
    VideoCameraOutlined,
} from '@ant-design/icons';

const { Header, Content, Footer, Sider } = Layout;
export default function Admin(props) {

  const [noSidebarLayout, setNoSidebarLayout] = useState(false);

  const getRoutes = (routes) => {
    // if (!props.isAuthenticated) {
    //   return <Redirect to={{
    //       pathname: '/auth/login',
    //       state: { from: props.location }
    //     }}
    //   />
    // }
    console.log("asd")
    return routes.map((prop, key) => {
      if (prop.layout === ENUMS.ROUTES.ADMIN) {
        return (
          <Route
            path={prop.layout + prop.path}
            component={prop.component}
            key={key}
          />
        );
      } else {
        return null;
      }
    });
  };

  const getBrandText = (path) => {
    for (let i = 0; i < routes.length; i++) {
      if (
        path.indexOf(
          routes[i].layout + routes[i].path
        ) !== -1
      ) {
        return routes[i].name;
      }
    }
    return "Brand";
  };

  useEffect(() => {
    if (APP_CONSTANTS.ROUTES_WITHOUT_SIDEBAR.indexOf(props.location.pathname) > -1) {
      setNoSidebarLayout(true);
    }
  }, [props])
  return (
    <>
        {
            noSidebarLayout
            ? <Layout className="layout">
                <Header>
                    <div className="logo">
                    </div>
                    <Menu theme="dark" mode="horizontal" className="justify-end">
                        <Menu.Item>{`Account Settings`}</Menu.Item>
                    </Menu>
                </Header>
                <Content style={{ padding: '0 50px' }}>
                    <Breadcrumb style={{ margin: '16px 0' }}>
                        <Breadcrumb.Item>Project</Breadcrumb.Item>
                        {/* <Breadcrumb.Item>List</Breadcrumb.Item>
                        <Breadcrumb.Item>App</Breadcrumb.Item> */}
                    </Breadcrumb>
                    <div className="site-layout-content mb-12">
                        <Switch>
                            {getRoutes(routes)}
                            <Redirect from="*" to="/admin/dashboard" />
                        </Switch>
                    </div>
                </Content>
            </Layout>
            : <Layout>
                <Sider
                    style={{
                        overflow: 'auto',
                        height: '100vh',
                        position: 'fixed',
                        left: 0,
                    }}
                >
                    <div className="logo">Seismos</div>
                    <Menu theme="" mode="inline" defaultSelectedKeys={['4']}>
                        <Menu.Item key="1" icon={<UserOutlined />}>
                        nav 1
                        </Menu.Item>
                        <Menu.Item key="2" icon={<VideoCameraOutlined />}>
                        nav 2
                        </Menu.Item>
                        <Menu.Item key="3" icon={<UploadOutlined />}>
                        nav 3
                        </Menu.Item>
                        <Menu.Item key="4" icon={<BarChartOutlined />}>
                        nav 4
                        </Menu.Item>
                        <Menu.Item key="5" icon={<CloudOutlined />}>
                        nav 5
                        </Menu.Item>
                        <Menu.Item key="6" icon={<AppstoreOutlined />}>
                        nav 6
                        </Menu.Item>
                        <Menu.Item key="7" icon={<TeamOutlined />}>
                        nav 7
                        </Menu.Item>
                        <Menu.Item key="8" icon={<ShopOutlined />}>
                        nav 8
                        </Menu.Item>
                    </Menu>
                </Sider>
                <Layout className="site-layout" style={{ marginLeft: 200 }}>
                    <Header>
                        <Menu theme="dark" mode="horizontal" className="justify-end">
                            <Menu.Item>{`Account Settings`}</Menu.Item>
                        </Menu>
                    </Header>
                    <Content style={{ margin: '24px 16px 0', overflow: 'initial' }}>
                    </Content>
                    <Footer style={{ textAlign: 'center' }}>Ant Design Â©2018 Created by Ant UED</Footer>
                </Layout>
            </Layout>
        }
    </>
  );
}
