import { SET_USER_STATE, USER_LOGGED_OUT, SET_USER_PROJECT } from "redux/actionsTypes";

const initialState = {
    user: {},
    userProjectIds: null
};

export default function authReducer(state = initialState, action) {
  switch (action.type) {
    case SET_USER_STATE: {
        const user = action.payload.user;
        return {
            ...state,
            user: { ...user}
        }
    }
    case USER_LOGGED_OUT: {
        return {
            ...state,
            user: {},
            userProjectIds: null
        }
    }
    case SET_USER_PROJECT : {
        const projectIds = action.payload.projectIds;
        return {
            ...state,
            userProjectIds: [...projectIds]
        }
    }
    default:
      return state;
  }
}
