import React, { useState } from "react";
import { Card, PageHeader, Button, Steps, Form, Input, Checkbox } from 'antd';
import { useHistory } from "react-router";
import JobInfo from "components/Forms/ProjectCreate/JobInfo";
import WellInfo from "components/Forms/ProjectCreate/WellInfo";
import WellVolume from "components/Forms/ProjectCreate/WellVolume";
import ClientInfo from "components/Forms/ProjectCreate/ClientInfo";
import CrewInfo from "components/Forms/ProjectCreate/CrewInfo";
import Equipment from "components/Forms/ProjectCreate/Equipment";

// components
const { Step } = Steps;

export default function NewProject() {
    const history = useHistory();

    const [current, setCurrent] = useState(0);
    const [jobInfoValues, setJobInfoValues] = useState();
    const [wellInfoValues, setWellInfoValues] = useState();
    const [wellVolumeValues, setWellVolumeValues] = useState();
    const [clientInfoValues, setClientInfoValues] = useState();
    const [crewInfoValues, setCrewInfoValues] = useState();
    const [equipmentValues, setEquipmentValues] = useState();
    const next = () => {
        setCurrent(current + 1);
    };

    const prev = () => {
        setCurrent(current - 1);
    };

    const steps = [
        {
            title: 'Job Info',
            content: <JobInfo next={next} setFormValue={setJobInfoValues} formValues={jobInfoValues}></JobInfo>
        },
        {
            title: 'Well Info',
            content: <WellInfo next={next} setFormValue={setWellInfoValues} formValues={wellInfoValues}></WellInfo>
        },
        {
            title: 'Well Volume',
            content: <WellVolume next={next} setFormValue={setWellVolumeValues} formValues={wellVolumeValues}></WellVolume>
        },
        {
            title: 'Client Info',
            content: <ClientInfo next={next} setFormValue={setClientInfoValues} formValues={clientInfoValues}></ClientInfo>
        },
        {
            title: 'Crew Info',
            content: <CrewInfo next={next} setFormValue={setCrewInfoValues} formValues={crewInfoValues}></CrewInfo>
        },
        {
            title: 'Equipment',
            content: <Equipment next={next} setFormValue={setEquipmentValues} formValues={equipmentValues}></Equipment>
        }
    ];

    return (
        <>
            <div style={{minHeight: "80vh"}} className="flex justify-center items-center bg-white">
                <div style={{width: "900px"}}>
                    <PageHeader
                        ghost={false}
                        onBack={() => window.history.back()}
                        title="Fill in the information below to create a new project"
                    >
                    </PageHeader>
                        <Card>
                            <Steps progressDot current={current}>
                                {
                                    steps.map(item => (
                                        <Step key={item.title} title={item.title} className="mb-6"/>
                                    ))
                                }
                            </Steps>
                            <div className="steps-content mb-6">
                                {steps[current].content}
                            </div>
                            <div className="steps-action flex justify-between" style={{position: 'absolute', bottom: '48px'}}>
                                {
                                    <Button style={{ margin: '0 8px' }} disabled={current < 1} onClick={() => prev()}>
                                        Previous
                                    </Button>
                                }
                                {/* {current < steps.length - 1 && (
                                <Button type="primary" onClick={() => next()}>
                                    Next
                                </Button>
                                )} */}
                                {current === steps.length - 1 && (
                                <Button type="primary" onClick={(e) => console.log(e)}>
                                    Done
                                </Button>
                                )}
                            </div>
                        </Card>
                </div>
            </div>
        </>
    );
}
