import routes from "routes/routes.js";
import { useSelector } from "react-redux";
import { Layout, Menu, Breadcrumb } from 'antd';
import React, { useEffect, useState } from "react";
import { Switch, Route, Redirect } from "react-router-dom";

// components
import ENUMS from "constants/appEnums";
import APP_CONSTANTS from "constants/appConstants";
import AdminNavbar from "components/Navbars/AdminNavbar.js";
import {
    AppstoreOutlined,
    BarChartOutlined,
    CloudOutlined,
    ShopOutlined,
    TeamOutlined,
    UserOutlined,
    UploadOutlined,
    VideoCameraOutlined,
    MailOutlined
} from '@ant-design/icons';

const { Content, Footer, Sider } = Layout;
const { SubMenu } = Menu;

export default function Admin(props) {

    const [noSidebarLayout, setNoSidebarLayout] = useState(false);
    const user = useSelector(state => state.authReducer.user);
    const project = useSelector(state => state.authReducer.userProject);

    const getRoutes = (routes) => {
        // if (!props.isAuthenticated) {
        //   return <Redirect to={{
        //       pathname: '/auth/login',
        //       state: { from: props.location }
        //     }}
        //   />
        // }
        return routes.map((prop, key) => {
        if (prop.layout === ENUMS.ROUTES.ADMIN) {
            return (
            <Route
                path={prop.layout + prop.path}
                component={prop.component}
                key={key}
            />
            );
        } else {
            return null;
        }
        });
    };

    useEffect(() => {
        if (APP_CONSTANTS.ROUTES_WITHOUT_SIDEBAR.indexOf(props.location.pathname) > -1) {
        setNoSidebarLayout(true);
        } else {
            setNoSidebarLayout(false);
        }
    }, [props])
    return (
        <>
            {
                noSidebarLayout
                ? <Layout className="layout">
                    <AdminNavbar withLogo={true} />
                    <Content style={{ padding: '0 50px' }}>
                        <Breadcrumb style={{ margin: '16px 0' }}>
                            <Breadcrumb.Item>Project</Breadcrumb.Item>
                            {/* <Breadcrumb.Item>List</Breadcrumb.Item>
                            <Breadcrumb.Item>App</Breadcrumb.Item> */}
                        </Breadcrumb>
                        <div className="site-layout-content mb-12 p-5 bg-white">
                            <Switch>
                                {getRoutes(routes)}
                                <Redirect from="*" to="/admin/dashboard" />
                            </Switch>
                        </div>
                    </Content>
                </Layout>
                : <Layout>
                    <Sider
                        style={{
                            overflow: 'auto',
                            height: '100vh',
                            position: 'fixed',
                            left: 0,
                        }}
                        width={250}
                        className="site-layout-background"
                    >
                        <div className="logo">Seismos</div>
                        <Menu theme="" mode="inline" defaultSelectedKeys={['1']} defaultOpenKeys={['sub1']} className="sidebar-margin-top">
                            <SubMenu key="sub1" icon={<MailOutlined />} title="Navigation One">
                                <Menu.ItemGroup key="g1" title="Item 1">
                                    <Menu.Item key="1">Option 1</Menu.Item>
                                    <Menu.Item key="2">Option 2</Menu.Item>
                                </Menu.ItemGroup>
                                <Menu.ItemGroup key="g2" title="Item 2">
                                    <Menu.Item key="3">Option 3</Menu.Item>
                                    <Menu.Item key="4">Option 4</Menu.Item>
                                </Menu.ItemGroup>
                            </SubMenu>
                        </Menu>
                    </Sider>
                    <Layout className="site-layout" style={{ marginLeft: 200 }}>
                        <AdminNavbar withLogo={false} />
                        <Content style={{ margin: '24px 16px 0', overflow: 'initial' }}>
                        </Content>
                        <Footer style={{ textAlign: 'center' }}>Ant Design Â©2018 Created by Ant UED</Footer>
                    </Layout>
                </Layout>
            }
        </>
    );
}
