import React, { useState } from "react";
import { Form, Table, Button } from 'antd';
import TableHeadersUtil from "util/TableHeaderUtil";
import ProjectUtil from "util/ProjectUtil";
import Grid from "components/Grid/Grid";

export default function JobInfo(props) {
    const [form] = Form.useForm();

    const [jobInfoGrid, setJobInfoGrid] = useState([]);

    const rowLabels = [
        'Job name',
        'Pad name',
        'Job type',
        'Pad Id',
        'Job id',
        'Client name',
        'AFE #',
        'Customer field rep',
        'Country',
        'Rep contact #',
        'Basin',
        'Operator',
        'State',
        'Service company',
        'Start date',
        'Wireline company'
    ];

    const populateJobInfoGrid = () => {
        setJobInfoGrid(TableHeadersUtil.jobInfoFormTableData.grid.reduce((rows, t, rowIdx) => {
            console.log(t)
            const row = t.rows.map(label => {
                return [
                    { value: label, readOnly: true, className: 'testingclass', disableEvents: true },
                    { value: "" },
                ]
            })
            console.log(row)
            return rows.concat(row);
        }, []))
    }

    useState(() => {
        props.formValues && form.setFieldsValue(props.formValues)
        populateJobInfoGrid();
    }, props);

    return (
        <>
            <div class="flex">
                <div className="mb-8">
                    <Grid 
                        columns={TableHeadersUtil.jobInfoFormTableData.columns}
                        grid={jobInfoGrid}
                        className="job-info-grid-left"
                    ></Grid>
                </div>
                <div className="mb-8">
                    <Grid 
                        columns={TableHeadersUtil.jobInfoFormTableData.columns}
                        grid={jobInfoGrid}
                        className="job-info-grid-left"
                    ></Grid>
                </div>
            </div>
            <div className="text-right">
                <Button type="primary" onClick={(e) => {props.next()}}>
                    Next
                </Button>
            </div>
        </>
    );
}
