import React, { useState } from "react";
import { Form, Button, Space } from 'antd';
import TableHeadersUtil from "util/TableHeaderUtil";
import Grid from "components/Grid/Grid";

export default function JobInfo(props) {
    const [form] = Form.useForm();

    const [jobInfoGrid, setJobInfoGrid] = useState([]);
    const [padInfoGrid, setPadInfoGrid] = useState([]);

    const getRow = (t) => {
        return t.rows.map(label => {
            return [
                { value: label, readOnly: true, disableEvents: true },
                { value: "" },
            ]
        })
    }

    const populateJobInfoGrid = () => {
        setJobInfoGrid(TableHeadersUtil.jobInfoFormTableData.grid.reduce((rows, t, rowIdx) => {
            const row = getRow(t);
            return rows.concat(row);
        }, []))
    }

    const populatePadInfoGrid = () => {
        setPadInfoGrid(TableHeadersUtil.padInfoFormTableData.grid.reduce((rows, t, rowIdx) => {
            const row = getRow(t);
            return rows.concat(row);
        }, []))
    }

    const handleGridChanged = (data) => {

    }

    useState(() => {
        props.formValues && form.setFieldsValue(props.formValues)
        populateJobInfoGrid();
        populatePadInfoGrid();
    }, props);

    return (
        <>
            <div className="flex">
                <Space size="large">
                    <Grid 
                        columns={TableHeadersUtil.jobInfoFormTableData.columns}
                        grid={jobInfoGrid}
                        className="job-info-grid mb-8"
                        gridValueChanged={handleGridChanged}
                    ></Grid>
                    <Grid 
                        columns={TableHeadersUtil.padInfoFormTableData.columns}
                        grid={padInfoGrid}
                        className="pad-info-grid mb-8"
                    ></Grid>
                </Space>
            </div>
            <div className="text-right">
                <Button type="primary" onClick={(e) => {props.next()}}>
                    Next
                </Button>
            </div>
        </>
    );
}
