import axios from "axiosConfig";
import config from "config";
import ENUMS from "constants/appEnums";
import React, { useState } from "react";
import { useForm } from "react-hook-form";
import allActions from 'redux/actions/index';
import { useAppContext } from "util/ContextUtil";
import { Link, useHistory, useLocation } from "react-router-dom";
import { useDispatch } from "react-redux";
import { Form, Input, Card, Divider, Button, Checkbox, Row, Col } from 'antd';
import { UserOutlined, LockOutlined } from '@ant-design/icons';
import { useToasts } from "react-toast-notifications";

export default function Login() {
    let history = useHistory();
    let location = useLocation();
    const { addToast } = useToasts();
    const dispatch = useDispatch();
    const { handleSubmit } = useForm({});
    const { userHasAuthenticated } = useAppContext();

    // state variables
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    /**
     * Method to make API call for user login
     */
    const handleLogin = (data) => {
        saveUserState({
            token: 'test-token',
            user: {
                first_name: 'Test',
                last_name: 'user'
            },
            project: null
    })

        // axios.post(config.API_URL + ENUMS.API_ROUTES.AUTH_LOGIN,
        //     {
        //         username: data.username,
        //         password: data.password
        //     })
        //     .then(res => {
        //         console.log(res);
        //         if (res.status === 200 && res.data) {
        //             saveUserState(res.data.data);
        //         }
        //     })
        //     .catch(e => {
        //         if(data.username == "bobo" && data.password == "1234") {
        //             saveUserState({
        //                 token: 'test-token',
        //                 user: {
        //                     first_name: 'Test',
        //                     last_name: 'user'
        //                 },
        //                 project: null
        //             })
        //         } else {
        //             addToast("Incorrect username or password!", { appearance: 'error' });
        //         }
        //         console.log(e)
        //     });
    }

    /**
     * Method to save user data in redux store and set local storage with JWT
     * @param {Object} data 
     */
    const saveUserState = (data) => {
        localStorage.setItem('JWT', data.access_token);
        userHasAuthenticated(true);
        dispatch(allActions.authActions.setUserState(data.user));
        dispatch(allActions.authActions.setUserProject(data.project_ids));

        if (location && location.state && location.state.from) {
            history.push({
                pathname: location.state.from.pathname,
                search: location.state.from.search,
                state: location.state.from.state
            });
        } else {
            history.push(ENUMS.ROUTES.ADMIN + ENUMS.ROUTES.PROJECT);
        }
    }

    return (
        <>
            <div className="container mx-auto px-4 h-full login-container">
                <div className="flex content-center items-center justify-center h-full">
                    <div className="w-full lg:w-4/12 px-4">
                        <Card className="text-center login-header-card">
                            <img alt="seismos logo" className="mx-auto" src={require("assets/img/seismos/seismos_logo_animated.gif").default}></img>
                        </Card>
                        <Card>
                            <h6 className="text-blueGray-500 text-sm text-center">
                                Log in to Seismos
                            </h6>
                            <Divider></Divider>
                            <Form
                                name="normal_login"
                                className="login-form"
                                initialValues={{
                                    remember: true,
                                }}
                                onFinish={handleLogin}
                                >
                                <Form.Item
                                    name="username"
                                    rules={[
                                    {
                                        required: true,
                                        message: 'Please input your Username!',
                                    },
                                    ]}
                                >
                                    <Input prefix={<UserOutlined className="site-form-item-icon" />} placeholder="Username" />
                                </Form.Item>
                                <Form.Item
                                    name="password"
                                    rules={[
                                    {
                                        required: true,
                                        message: 'Please input your Password!',
                                    },
                                    ]}
                                >
                                    <Input
                                    prefix={<LockOutlined className="site-form-item-icon" />}
                                    type="password"
                                    placeholder="Password"
                                    />
                                </Form.Item>
                                <Row gutter={24} className="mb-6">
                                    <Col span={12}>
                                        <Form.Item name="remember" noStyle>
                                            <Checkbox>Remember me</Checkbox>
                                        </Form.Item>
                                    </Col>

                                </Row>

                                <Form.Item className="text-center">
                                    <Button type="primary" htmlType="submit" className="w-full login-form-button">
                                    Log in
                                    </Button>


                                </Form.Item>
                            </Form>
                        </Card>
                        <div className="flex flex-wrap mt-2 relative justify-between">
                            <div className="w-1/2">
                                <a
                                    href="#pablo"
                                    onClick={(e) => e.preventDefault()}
                                    className="text-seismos-primary"
                                >
                                    Forgot password?
                                </a>
                            </div>
                            <div className="w-1/2 text-right">
                                <Link to="/auth/register" className="block text-seismos-primary">
                                    Create new account
                                </Link>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}
