import React, { useState } from "react";
import { Form, Table, Button } from 'antd';
import TableHeadersUtil from "util/TableHeaderUtil";
import ProjectUtil from "util/ProjectUtil";
import Grid from "./Grid";

export default function WellInfo(props) {
    const [form] = Form.useForm();

    const [wellInfoGrid, setWellInfoGrid] = useState([]);

    const onFinish = values => {
        props.setFormValue(values);
        props.next();
    };

    const handlePaste = (e, form, fieldIndex) => {
        var data = e.clipboardData.items[0];

        const values = [];
        data.getAsString(str => {
            str.split(/\s/).forEach((v) => {
              values.push(v);
            })
            form.setFieldsValue(ProjectUtil.setClipboardValues(form.getFieldsValue(), values, fieldIndex));
        });
    }

    const populateWillInfoGrid = () => {
        setWellInfoGrid(TableHeadersUtil.wellInfoFormTableData.grid.reduce((rows, t, rowIdx) => {
            const rowValues = [
                [
                    { value: "Well 1" },
                    { value: "" },
                    { value: "" },
                    { value: "" },
                    { value: "" },
                    { value: "" },
                ]
            ]

            return rows.concat(rowValues);
        }, []))
    }

    useState(() => {
        props.formValues && form.setFieldsValue(props.formValues)
        populateWillInfoGrid();
    }, props);

    return (
        <>
            <div className="mb-8">
                <Grid 
                    columns={TableHeadersUtil.wellInfoFormTableData.columns}
                    grid={wellInfoGrid}
                ></Grid>
            </div>
            <div className="text-right">
                <Button type="primary" onClick={(e) => {props.next()}}>
                    Next
                </Button>
            </div>
        </>
    );
}
