import React, { useState } from "react";
import { Button } from 'antd';
import TableHeadersUtil from "util/TableHeaderUtil";
import Grid from "components/Grid/Grid";
import _ from "lodash";

export default function ClientInfo(props) {

    const [clientInfoGrid, setClientInfoGrid] = useState([]);

    const getClientInfoGridRow = (t) => {
        return t.rows.map(label => {
            const columns = [];
            TableHeadersUtil.clientInfoFormTableData.columns.map(column => {
                columns.push({
                    value: "",
                    field: column.field,
                    required: column.required,
                    component: column.component,
                    forceComponent: column.forceComponent ,
                    className: column.className
                });
                return column;
            })
            return columns;
        })
    }

    const populateClientInfoGrid = () => {
        setClientInfoGrid(TableHeadersUtil.clientInfoFormTableData.grid.reduce((rows, t, rowIdx) => {
            return rows.concat(getClientInfoGridRow(t));
        }, []));
    }

    const addNewClient = (e) => {
        e.preventDefault();
        const columns = [];
        TableHeadersUtil.clientInfoFormTableData.columns.map(column => {
            columns.push({ 
                value: "",
                field: column.field,
                required: column.required,
                component: column.component,
                forceComponent: column.forceComponent
            });
            return column;
        })
        const newClientInfoGrid = [
            ...clientInfoGrid,
            columns
        ];
        setClientInfoGrid(newClientInfoGrid);
    }

    const handleClientInfoGridChanged = (updatedGridData) => {
        props.setFormValue(updatedGridData);
        setClientInfoGrid(updatedGridData);
    }

    const gotoNextStep = () => {
        let isClientInfoValid = true;
        const validatedClientGrid = _.cloneDeep(clientInfoGrid);
        validatedClientGrid.map(row => {
            row.map(cell => {
                if(cell.required && cell.value === "") {
                    isClientInfoValid = false;
                    cell.className = 'cell-error';
                }
                return cell;
            })
            return row;
        });

        if(!isClientInfoValid) {
            handleClientInfoGridChanged(validatedClientGrid);
        } else {
            props.next();
        }
    }

    useState(() => {
        if(props.formValues != null) {
            props.formValues && setClientInfoGrid(props.formValues);
        } else {
            populateClientInfoGrid();
        }
    }, props);

    return (
        <>
            <div className="mb-8" style={{width: '60%', marginLeft: 'auto', marginRight: 'auto'}}>
                <Grid 
                    columns={TableHeadersUtil.clientInfoFormTableData.columns}
                    grid={clientInfoGrid}
                    gridValueChanged={handleClientInfoGridChanged}
                ></Grid>
                <div className="text-lightBlue-600 mt-2 cursor-pointer inline-block" onClick={(e) => addNewClient(e)}>+ Add a new client</div>
            </div>
            <div className="text-right">
                <Button type="primary" onClick={(e) => {gotoNextStep()}}>
                    Next
                </Button>
            </div>
        </>
    );
}
