import React, { useState } from "react";
import { Form, Button } from 'antd';
import TableHeadersUtil from "util/TableHeaderUtil";
import Grid from "components/Grid/Grid";

export default function WellVolume(props) {
    const [form] = Form.useForm();

    const [wellVolumeGrid, setWellVolumeGrid] = useState([]);
    const [wellVolumeEstimationsGrid, setWellVolumeEstimationsGrid] = useState([]);

    const getWellVolumeGridRow = (t) => {
        return t.rows.map(label => {
            return [
                { value: label },
                { value: "" },
                { value: "" },
                { value: "" },
                { value: "" },
                { value: "" },
            ]
        })
    }

    const getWellVolumeEstimationsGridRow = (t) => {
        return t.rows.map(label => {
            return [
                { value: "" },
                { value: label, readOnly: true, disableEvents: true },
            ]
        })
    }

    const populateWellVolumeGrid = () => {
        setWellVolumeGrid(TableHeadersUtil.wellVolumeFormTableData.grid.reduce((rows, t, rowIdx) => {
            return rows.concat(getWellVolumeGridRow(t));
        }, []))
    }

    const populateWellVolumeEstimationsGrid = () => {
        setWellVolumeEstimationsGrid(TableHeadersUtil.wellVolumeEstimationsFormTableData.grid.reduce((rows, t, rowIdx) => {
            return rows.concat(getWellVolumeEstimationsGridRow(t));
        }, []))
    }

    const handleWellVolumeGridChanged = (updatedGridData) => {
        setWellVolumeGrid(updatedGridData)
    }

    const gotoNextStep = () => {
        props.next();
    }

    useState(() => {
        props.formValues && form.setFieldsValue(props.formValues)
        populateWellVolumeGrid();
        populateWellVolumeEstimationsGrid();
    }, props);

    return (
        <>
            <div className="flex">
                <div className="well-volume-grid mb-8">
                    <Grid 
                        columns={TableHeadersUtil.wellVolumeFormTableData.columns}
                        grid={wellVolumeGrid}
                        gridValueChanged={handleWellVolumeGridChanged}
                    ></Grid>
                </div>
                <div className="p-4"></div>
                <div className="well-volume-estimation-grid mb-8">
                    <Grid
                        columns={TableHeadersUtil.wellVolumeEstimationsFormTableData.columns}
                        grid={wellVolumeEstimationsGrid}
                    ></Grid>
                </div>
            </div>
            <div className="text-right">
                <Button type="primary" onClick={(e) => {props.next()}}>
                    Next
                </Button>
            </div>
        </>
    );
}
