import React from "react";
import ReactDOM from "react-dom";

import ReactDataSheet from "react-datasheet";
import "react-datasheet/lib/react-datasheet.css";

const groupings = [
  {
    title: "Assets",
    descriptions: [
      "$ Allowance for Loan Losses",
      "$ Restricted Cash",
    ]
  },
  {
    title: "LIABILITIES",
    descriptions: [
      "$ Allowance for Loan Losses",
      "$ Restricted Cash",
    ]
  },
  {
    title: "NET ASSETS",
    descriptions: [
      "$ Total Secured Debt ",
      "$ Total Debt ",
    ]
  },
  {
    title: "EXPENSES",
    descriptions: [
      "$ Available Financing Capital",
      "$ Total Financing Capital",
    ]
  },
  {
    title: "INCOME/REVENUE",
    descriptions: [
      "$ Interest Expense or Total Interest Expense",
      "$ Operating Expense from: Provision for Loan Losses",
    ]
  },
  {
    title: "CAPITALIZATION",
    descriptions: [
      "$ Government Grants",
      "$ Total Grants ",
    ]
  },
  {
    title: "Assets2",
    descriptions: [
      "$ Borrowed Funds from: Individuals",
      "$ Borrowed Funds from: Religious Institutions",
    ]
  }
];

const SheetRenderer = props => {
  const { className, columns } = props;
  return (
    <table className={className}>
      <thead>
        <tr>
          {columns.map((col, index) => (
            <th
              className={`cell ${className}`}
              style={{
                width: col.width,
                background: "#f9f9f9",
                padding: "10px 0px",
                textAlign: "center"
              }}
            >
              {col.label}
            </th>
          ))}
        </tr>
      </thead>
      <tbody>{props.children}</tbody>
    </table>
  );
};

const CellRenderer = props => {
  const { children } = props;
  return (
    <td className="cell" style={{ textAlign: "center" }}>
      {children}
    </td>
  );
};

class Grid extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      columns: [
        { label: "Well name", width: "15%" },
        { label: "Well API", width: "17%" },
        { label: "Formation", width: "17%" },
        { label: "Lat", width: "17%" },
        { label: "Easting", width: "17%" },
        { label: "Northing", width: "17%" },
      ],
      grid: groupings.reduce((rows, t, rowIdx) => {
        const descripts = t.descriptions.map((d, idx) => {
          return [
            { value: idx === 0 ? t.title : "" },
            { value: d },
            { value: "$0.00" },
            { value: "$0.00" },
            { value: "$0.00" },
            { value: "$0.00" },
          ];
        });

        return rows.concat(descripts);
      }, [])
    };

    this.renderSheet = this.renderSheet.bind(this);
    this.renderRow = this.renderRow.bind(this);
  }

  renderSheet(props) {
    return <SheetRenderer columns={this.state.columns} {...props} />;
  }

  renderRow(props) {
    const { row, cells, ...rest } = props;
    return <CellRenderer rowIndex={row} {...rest} />;
  }

  render() {
    console.log(this.state.grid);
    return (
      <div style={{ width: "100%" }}>
        <ReactDataSheet
          data={this.state.grid}
          valueRenderer={cell => cell.value}
          sheetRenderer={this.renderSheet}
          // cellRenderer={this.renderRow}
          onCellsChanged={changes => {
            const grid = this.state.grid.map(row => [...row]);
            changes.forEach(({ cell, row, col, value }) => {
              grid[row][col] = { ...grid[row][col], value };
            });
            this.setState({ grid });
          }}
        />
      </div>
    );
  }
}

export default Grid;
