import React, { useEffect, useState } from "react";
import { Card, Table, PageHeader, Button} from 'antd';
import FAKE_DATA from "constants/fakeData";
import TableHeadersUtil from "util/TableHeaderUtil";
import { useHistory } from "react-router";
import { useDispatch } from "react-redux";
import allActions from 'redux/actions/index';
// components
export default function ExistingProject() {
    const history = useHistory();
    const dispatch = useDispatch();
    const columns = TableHeadersUtil.projectTable;
    const data = FAKE_DATA.LIST_OF_PROJECTS;

    const [selectedRow, setSelectedRow] = useState(null);

    const rowSelection = {
        onChange: (selectedRowKeys, selectedRows) => {
          setSelectedRow(data.filter(x => x.key === selectedRowKeys));
        },
        getCheckboxProps: (record) => ({
          disabled: record.name === 'Disabled User',
          // Column configuration not to be checked
          name: record.name,
        }),
    };

    const handleTableChange = (pagination, filters, sorter) => {
        // this.fetch({
        //   sortField: sorter.field,
        //   sortOrder: sorter.order,
        //   pagination,
        //   ...filters,
        // });
    };

    const [pagination, setPagination] = useState({});

    const openProject = (e) => {
        e.preventDefault();
        dispatch(allActions.authActions.setUserProject(FAKE_DATA.projectData));
        history.push('/admin/dashboard');
    }

    useEffect(() => {
        setPagination({
            current: 1,
            pageSize: 5,
            total: 10,
        })
    }, [])

    return (
        <>
            <div style={{minHeight: "80vh"}} className="flex justify-center items-center bg-white">
                <div style={{width: '1100px'}}>
                <PageHeader
                    ghost={false}
                    onBack={() => window.history.back()}
                    title="Select a project to continue"
                    extra={[
                        <Button
                            key="1"
                            type="primary"
                            disabled={selectedRow ? false : true}
                            onClick={e => openProject(e)}
                        >
                            Next
                        </Button>,
                    ]}
                    >
                </PageHeader>
                    <Card>
                        <Table
                            rowSelection={{
                                type: 'radio',
                                ...rowSelection,
                            }}
                            pagination={pagination}
                            columns={columns}
                            dataSource={data}
                            onChange={handleTableChange}
                        />
                    </Card>
                </div>
            </div>
        </>
    );
}
