import React, { useState } from "react";
import { Card, PageHeader, Button, Steps, Form, Input, Checkbox } from 'antd';
import { useHistory } from "react-router";

// components

const { Step } = Steps;

export default function NewProject() {
    const history = useHistory();

    const [stepIndex, setStepIndex] = useState(0);

    const stepChange = (e) => {
        setStepIndex(e)
    }

    const nextStep = (e) => {
        e.preventDefault();
        if (stepIndex != 5) {
            setStepIndex(stepIndex + 1)
        } else {
            history.push('/admin/dashboard');
        }
    }

    const previousStep = (e) => {
        e.preventDefault();
        stepIndex !=0 && setStepIndex(stepIndex -1);
    }

    const onFinish = (values) => {
        console.log('Success:', values);
    };

      const onFinishFailed = (errorInfo) => {
        console.log('Failed:', errorInfo);
    };

    return (
        <>
            <div style={{minHeight: "80vh"}} className="flex justify-center items-center bg-white">
                <div style={{width: "900px"}}>
                <PageHeader
                    ghost={false}
                    onBack={() => window.history.back()}
                    title="Fill in the information below to create a new project"
                    extra={[
                        <Button
                            key="1"
                            type="primary"
                        >
                            Next
                        </Button>,
                    ]}
                    >
                </PageHeader>
                    <Card>
                        <Steps progressDot current={stepIndex} onChange={(e) => stepChange(e)}>
                            <Step title="Job Info" />
                            <Step title="Well Info" />
                            <Step title="Well Volume" />
                            <Step title="Client Info" />
                            <Step title="Crew Info" />
                            <Step title="Equipment" />
                        </Steps>
                        <div className="">
                            <Input placeholder="Basic usage" />
                        </div>
                        <div className="flex justify-between">
                            <Button type="secondary" onClick={e => previousStep(e)} disabled={stepIndex == 0}>Back</Button>
                            <Button type="primary" onClick={e => nextStep(e)}>Next</Button>
                        </div>
                    </Card>
                </div>
            </div>
        </>
    );
}
