import React, { useState } from "react";
import { Card, Row, Col, Button } from 'antd';
import APP_CONSTANTS from "constants/appConstants";
import _ from "lodash";

export default function DataInput() {
    const [cards, setCards] = useState(APP_CONSTANTS.DATA_INPUT_CARDS);

    return (
        <>
            {
                cards.map((card, cardIndex) => {
                    return <Card key={cardIndex} title={card.title} bordered={false} style={{ width: '100%', marginBottom: '1.5rem' }} className="mb-6">
                        {
                            card.grid.map((cardGrid, cardGridIndex) => {
                                return <Card.Grid
                                    key={cardGridIndex} 
                                    style={{ width: '25%', textAlign: 'center'}}
                                >
                                    <div className="di-card-grid">{cardGrid.label}</div>
                                </Card.Grid>
                            })
                        }
                        {/* <Row gutter={16}>
                            {
                                card.grid.map((cardGrid, cardGridIndex) => {
                                    return <Col className="gutter-row" span={6}>
                                        <Button size={"large"} shape="round" className={"w-full " + cardGrid.color}>{cardGrid.label}</Button>
                                    </Col>
                                })
                            }
                        </Row>
                         */}
                    </Card>
                })
            }
        </>
    );
}
