import { SET_USER_STATE, USER_LOGGED_OUT, SET_USER_PROJECT } from "redux/actionsTypes";

const initialState = {
    user: {},
    userProject: null
};

export default function authReducer(state = initialState, action) {
  switch (action.type) {
    case SET_USER_STATE: {
        const user = action.payload.user;
        return {
            ...state,
            user: { ...user}
        }
    }
    case USER_LOGGED_OUT: {
        return {
            ...state,
            user: {},
            userProject: null
        }
    }
    case SET_USER_PROJECT : {
        const project = action.payload.project;
        return {
            ...state,
            userProject: {...project}
        }
    }
    default:
      return state;
  }
}
