import React, { useState } from "react";
import { Card, Space, Table, Button } from 'antd';
import FAKE_DATA from "constants/fakeData";
import _ from "loadash";

export default function OcReport() {
    const [data, setData] = useState(FAKE_DATA.STAGE_REPORT);

    const approveStage = (text, record) => {
        const index = data.findIndex(d => d.key === record.key);
        const newData = _.cloneDeep(data);
        newData[index].approved = true;
        setData(newData)
    }

    const columns = [
        {
            title: 'Stage',
            dataIndex: 'stage',
            key: 'stage',
        },
        {
            title: 'Pre Frac',
            dataIndex: 'pre_frac',
            key: 'pre_frac',
        },
        {
            title: 'Post Frac',
            dataIndex: 'post_frac',
            key: 'post_frac',
        },
        {
            title: 'NF',
            dataIndex: 'nf',
            key: 'nf',
        },
        {
            title: 'FF',
            dataIndex: 'ff',
            key: 'ff',
        },
        {
            title: 'Half length [ft]',
            dataIndex: 'half_length',
            key: 'half_length',
        },
        {
            title: 'Width [in]',
            dataIndex: 'width',
            key: 'width',
        },
        {
            title: 'Height',
            dataIndex: 'height',
            key: 'height',
        },
        {
            title: 'Reserve potential',
            dataIndex: 'reserve_potential',
            key: 'reserve_potential',
        },
        {
            title: 'Pore pressure',
            dataIndex: 'pore_pressure',
            key: 'pore_pressure',
        },
        {
            title: 'Action',
            key: 'action',
            render: (text, record) => (
                <Space size="middle">
                    {   record.approved 
                        ? 'Approved'
                        : <Button type="primary" onClick={(e) => approveStage(text, record)}>Approve</Button>
                    }
                </Space>
            ),
        },
    ];


    const renderBody = (props, columns) => {
        return (
          <tr className={props.className}>
            {columns.map((item, idx) => {
                console.log(item)
              if (!item.hidden) {
                return props.children[idx]
              }
            })}
          </tr>
        );
    }

    return (
        <>
            <Card>
                <Table
                    columns={columns}
                    dataSource={data}
                    components={{
                        body: {
                          row: (props) => renderBody(props, columns)
                        },
                      }}
                />
                <Button type="primary">Export stage report</Button>
            </Card>
        </>
    );
}
