import routes from "routes/routes.js";
import { useSelector } from "react-redux";
import { Layout, Menu, Breadcrumb, Spin } from 'antd';
import React, { useEffect, useState } from "react";
import { Switch, Route, Redirect, useHistory } from "react-router-dom";
import _ from "lodash";

// components
import ENUMS from "constants/appEnums";
import APP_CONSTANTS from "constants/appConstants";
import AdminNavbar from "components/Navbars/AdminNavbar.js";

const { Content, Sider } = Layout;
const { SubMenu } = Menu;

export default function Admin(props) {
    const history = useHistory();
    // const user = useSelector(state => state.authReducer.user);
    const project = useSelector(state => state.authReducer.userProject);

    const [noSidebarLayout, setNoSidebarLayout] = useState(false);
    const [sidebarMenu, setSidebarMenu] = useState([]);
    const [pages, setPages] = useState([]);

    const getRoutes = (routes) => {
        // if (!props.isAuthenticated) {
        //   return <Redirect to={{
        //       pathname: '/auth/login',
        //       state: { from: props.location }
        //     }}
        //   />
        // }
        return routes.map((prop, key) => {
        if (prop.layout === ENUMS.ROUTES.ADMIN) {
            return (
            <Route
                path={prop.layout + prop.path}
                component={prop.component}
                key={key}
            />
            );
        } else {
            return null;
        }
        });
    };



    useEffect(() => {
        if (APP_CONSTANTS.ROUTES_WITHOUT_SIDEBAR.indexOf(props.location.pathname) > -1) {
            setNoSidebarLayout(true);
            setPages(props.location.pathname.split("/admin/")[1].split("/")[0].split("-"));
        } else {
            setNoSidebarLayout(false);
        }
    }, [props]);

    useEffect(() => {
        if(!_.isEmpty(project)) {
            const menuChange = (path, id) => {
                history.push('/admin' + path + '/'+ id)
            }

            const wellInfo = project.wellInfoValues;
            const menu = wellInfo.map((well, index) => {
                return <SubMenu key={"sub"+(index+1)} title={well.well_name}>
                    <Menu.Item key={(index+1) + "1"} onClick={() => { menuChange(ENUMS.ROUTES.DATA_INPUT, well.id) }}>Data input</Menu.Item>
                    <Menu.Item key={(index+1) + "2"} onClick={() => { menuChange(ENUMS.ROUTES.DAILY_LOG, well.id) }}>Daily log</Menu.Item>
                    <Menu.Item key={(index+1) + "3"} onClick={() => { menuChange(ENUMS.ROUTES.DEFAULT_VALUES, well.id) }}>Default values</Menu.Item>
                    <Menu.Item key={(index+1) + "4"} onClick={() => { menuChange(ENUMS.ROUTES.TRACKING_SHEET, well.id) }}>Tracking sheet</Menu.Item>
                    <Menu.Item key={(index+1) + "5"} onClick={() => { menuChange(ENUMS.ROUTES.OC_REPORT, well.id) }}>OC report</Menu.Item>
                </SubMenu>
            });
            setSidebarMenu(menu);
        };
    }, [project, history]);


    return (
        <>
            {
                noSidebarLayout
                ? <Layout className="layout">
                    <AdminNavbar withLogo={true} />
                    <Content style={{ padding: '0 50px' }}>
                        <Breadcrumb style={{ margin: '16px 0' }}>
                            {
                                pages.map((page, index) => {
                                    return <Breadcrumb.Item key={index}><span className="capitalize">{page}</span></Breadcrumb.Item>
                                })
                            }

                            {/* <Breadcrumb.Item>List</Breadcrumb.Item>
                            <Breadcrumb.Item>App</Breadcrumb.Item> */}
                        </Breadcrumb>
                        <div className="site-layout-content mb-12 p-5 bg-white">
                            <Switch>
                                {getRoutes(routes)}
                                <Redirect from="*" to="/admin/dashboard" />
                            </Switch>
                        </div>
                    </Content>
                </Layout>
                : <Spin spinning={sidebarMenu.length < 1}>
                    <Layout>
                        <Sider
                            style={{
                                overflow: 'auto',
                                height: '100vh',
                                position: 'fixed',
                                left: 0,
                            }}
                            width={250}
                            className="site-layout-background"
                        >
                            <div className="logo"><img alt="seismos logo" src={require("assets/img/seismos/seismos_logo_animated.gif").default}></img></div>
                            <Menu theme="" mode="inline" defaultSelectedKeys={['11']} defaultOpenKeys={['sub1']} className="sidebar-margin-top">
                                {sidebarMenu}
                            </Menu>
                        </Sider>
                        <Layout className="site-layout" style={{ marginLeft: 250 }}>
                            <AdminNavbar withLogo={false} className="admin-navbar" />
                            <Content style={{ margin: '88px 16px 0', overflow: 'initial' }}>
                                <Switch>
                                    {getRoutes(routes)}
                                    <Redirect from="*" to="/admin/dashboard" />
                                </Switch>
                            </Content>
                        </Layout>
                    </Layout>
                </Spin>
            }
        </>
    );
}
