import React, { useState } from "react";
import { Form, Button } from 'antd';
import TableHeadersUtil from "util/TableHeaderUtil";
import Grid from "components/Grid/Grid";

export default function CrewInfo(props) {
    const [form] = Form.useForm();

    const [crewInfoGrid, setCrewInfoGrid] = useState([]);

    const getCrewInfoGridRow = (t) => {
        return t.rows.map(label => {
            return [
                { value: "" },
                { value: "" },
                { value: "", component: <input className="data-editor transparent-input" type="password"></input> },
            ]
        })
    }

    const populateCrewInfoGrid = () => {
        setCrewInfoGrid(TableHeadersUtil.crewInfoFormTableData.grid.reduce((rows, t, rowIdx) => {
            return rows.concat(getCrewInfoGridRow(t));
        }, []));
    }

    const addNewClient = (e) => {
        e.preventDefault();
        const newCrewInfoGrid = [
            ...crewInfoGrid,
            [
                { value: "" },
                { value: "" },
                { value: "", component: <input className="data-editor transparent-input" type="password"></input> }
            ]
        ];
        setCrewInfoGrid(newCrewInfoGrid);
    }

    const handleCrewInfoGridChanged = (updatedGridData) => {
        setCrewInfoGrid(updatedGridData)
    }

    useState(() => {
        props.formValues && form.setFieldsValue(props.formValues)
        populateCrewInfoGrid();
    }, props);

    return (
        <>
            <div className="mb-8">
                <Grid 
                    columns={TableHeadersUtil.crewInfoFormTableData.columns}
                    grid={crewInfoGrid}
                    gridValueChanged={handleCrewInfoGridChanged}
                ></Grid>
                <div className="text-lightBlue-600 mt-2 cursor-pointer inline-block" onClick={(e) => addNewClient(e)}>+ Add a new client</div>
            </div>
            <div className="text-right">
                <Button type="primary" onClick={(e) => {props.next()}}>
                    Next
                </Button>
            </div>
        </>
    );
}
