import axios from "axiosConfig";
import config from "config";
import ENUMS from "constants/appEnums";
import React, { useState } from "react";
import { useForm } from "react-hook-form";
import allActions from 'redux/actions/index';
import { useAppContext } from "util/ContextUtil";
import { Link, useHistory, useLocation } from "react-router-dom";
import { useDispatch } from "react-redux";

export default function Login() {
    let history = useHistory();
    let location = useLocation();
    const dispatch = useDispatch();
    const { handleSubmit } = useForm({});
    const { userHasAuthenticated } = useAppContext();

    // state variables
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    /**
     * Method to make API call for user login
     */
    const handleLogin = () => {
        // TODO: Remove fake call and data
        saveUserState({
            token: 'test-token',
            user: {
                first_name: 'Test',
                last_name: 'user'
            },
            projectList: []
        })
        // TODO ENDS : Remove fake call and data
        axios.post(config.API_URL + ENUMS.API_ROUTES.AUTH_LOGIN,
            {
                email: email,
                password: password
            })
            .then(res => {
                console.log(res);
                if (res.status === 200 && res.data.success) {
                    saveUserState(res.data);
                }
            })
            .catch(e => {
                console.log(e)
            });
    }

    /**
     * Method to save user data in redux store and set local storage with JWT
     * @param {Object} data 
     */
    const saveUserState = (data) => {
        localStorage.setItem('JWT', data.token);
        userHasAuthenticated(true);
        dispatch(allActions.authActions.setUserState(data.user));
        dispatch(allActions.authActions.setUserProject(data.projectList));

        if (location && location.state && location.state.from) {
            history.push({
                pathname: location.state.from.pathname,
                search: location.state.from.search,
                state: location.state.from.state
            });
        } else {
            history.push('/admin/project');
        }
    }

    return (
        <>
            <div className="container mx-auto px-4 h-full">
                <div className="flex content-center items-center justify-center h-full">
                    <div className="w-full lg:w-4/12 px-4">
                        <div className="relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded-lg bg-blueGray-200 border-0">
                        <div className="rounded-t mb-0 px-6 py-6">
                            <div className="text-center mb-3">
                            <h6 className="text-blueGray-500 text-sm font-bold">
                                Sign in
                            </h6>
                            </div>
                            <hr className="mt-6 border-b-1 border-blueGray-300" />
                        </div>
                        <div className="flex-auto px-4 lg:px-10 py-10 pt-0">
                            <form onSubmit={handleSubmit(handleLogin)}>
                                <div className="relative w-full mb-3">
                                    <label
                                        className="block uppercase text-blueGray-600 text-xs font-bold mb-2"
                                        htmlFor="grid-password"
                                        >
                                        Email
                                    </label>
                                    <input
                                        type="email"
                                        className="border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150"
                                        placeholder="Email"
                                        onChange={e => setEmail(e.target.value)}
                                    />
                                </div>

                                <div className="relative w-full mb-3">
                                    <label
                                        className="block uppercase text-blueGray-600 text-xs font-bold mb-2"
                                        htmlFor="grid-password"
                                    >
                                        Password
                                    </label>
                                    <input
                                        type="password"
                                        className="border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150"
                                        placeholder="Password"
                                        onChange={e => setPassword(e.target.value)}
                                    />
                                </div>
                                <div>
                                    <label className="inline-flex items-center cursor-pointer">
                                        <input
                                            id="customCheckLogin"
                                            type="checkbox"
                                            className="form-checkbox border-0 rounded text-blueGray-700 ml-1 w-5 h-5 ease-linear transition-all duration-150"
                                        />
                                        <span className="ml-2 text-sm font-semibold text-blueGray-600">
                                            Remember me
                                        </span>
                                    </label>
                                </div>

                                <div className="text-center mt-6">
                                    <button
                                        className="bg-blueGray-800 text-white active:bg-blueGray-600 text-sm font-bold uppercase px-6 py-3 rounded shadow hover:shadow-lg outline-none focus:outline-none mr-1 mb-1 w-full ease-linear transition-all duration-150"
                                        type="submit"
                                    >
                                    Sign In
                                    </button>
                                </div>
                            </form>
                        </div>
                        </div>
                        <div className="flex flex-wrap mt-6 relative">
                            <div className="w-1/2">
                                <a
                                    href="#pablo"
                                    onClick={(e) => e.preventDefault()}
                                    className="text-blueGray-200"
                                >
                                    <small>Forgot password?</small>
                                </a>
                            </div>
                            <div className="w-1/2 text-right">
                                <Link to="/auth/register" className="text-blueGray-200">
                                    <small>Create new account</small>
                                </Link>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}
