import React, { useState } from "react";

// components
import CardPageVisits from "components/Cards/CardPageVisits.js";
import Header from "components/Headers/Header.js";
import { useHistory } from "react-router";
import { Card, Button } from 'antd';
import { width } from "tailwindcss/defaultTheme";

export default function Project() {
    let history = useHistory();

    const [newProjectFocus, setNewProjectFocus] = useState(false);
    const [existingProjectFocus, setExistingProjectFocus] = useState(false);

    const handleExistingProject = () => {
        history.push('/admin/project-select');
    }

    const handleNewProject = () => {
        history.push('/admin/project-new');
    }

    const gridStyle = {
        width: '100%',
        textAlign: 'center'
    };
    return (
        <>
            <div style={{minHeight: "80vh"}} className="flex justify-center items-center">
                <div>
                    <Card title="Create a new project or choose an existing one" style={{width: '400px'}}>
                        <Card.Grid style={gridStyle} onMouseEnter={(e) => setNewProjectFocus(true)} onMouseLeave={() => setNewProjectFocus(false)} className={newProjectFocus ? 'bg-primary text-white cursor-pointer': ''}>
                            Create new project
                        </Card.Grid>
                        <Card.Grid style={gridStyle} onHover={(e) => setExistingProjectFocus(true)}>
                            Select existing project
                        </Card.Grid>
                    </Card>
                </div>
            </div>
        </>
    );
}
